CREATE SCHEMA IF NOT EXISTS app;

CREATE TABLE IF NOT EXISTS app.leave (
	id integer NOT NULL,
	task_id varchar(40) NOT NULL,
	task_description varchar(50),
	reason varchar(100),
	primary key(id)
);

CREATE TABLE IF NOT EXISTS app.form (
	id integer NOT NULL,
	
	approval_status varchar(20),
	json_data text,

	product_category varchar(20),
	product_no varchar(30),
	order_no varchar(30),
	client_no varchar(30),
	client_name varchar(30),

	apply_time timestamp,
	complete_time timestamp,
	timestamp_ timestamp,

	creator varchar(20),
	modifier varchar(20),
	create_time timestamp,
	modify_time timestamp,	

	primary key(id)
);

CREATE TABLE IF NOT EXISTS app.config (
	code varchar(60) NOT NULL,
	category varchar(60),
	value_ text, -- value 是 h2 保留字, 不可使用為欄位名稱
	description varchar(200),
	creator varchar(30),
	modifier varchar(30),
	create_time timestamp,
	modify_time timestamp,
	primary key(code)
);

